import React, { useEffect, useState } from 'react'
import { useMutation } from '@apollo/react-hooks'
import { Form, Input, Button } from 'antd'
import { UPDATE_INSTRUMENT } from '../../queries'

const UpdateInstrument = props => {
    const [id] = useState(props.id)
    const [year, setYear] = useState(props.year)
    const [brand, setBrand] = useState(props.brand)
    const [type, setType] = useState(props.type)
    const [price, setPrice] = useState(props.price)
    const [artistId, setArtistId] = useState(props.artistId)
    const [updateInstrument] = useMutation(UPDATE_INSTRUMENT)

    const [form] = Form.useForm()
    const [, forceUpdate] = useState()

    useEffect(() => {
        forceUpdate({})
    }, [])

    const onFinish = values => {
        const { firstName, lastName } = values
        updateInstrument({
            variables: {
                id,
                year,
                brand,
                type,
                price,
                artistId
            },
            optimisticResponse: {
                __typename: 'Mutation',
                updateInstrument: {
                    __typename: 'Instrument',
                    id,
                    year,
                    brand,
                    type,
                    price,
                    artistId
                }
            }
        })
        props.onButtonClick()
    }

    const updateStateVariable = (variable, value) => {
        switch (variable) {
            case 'year':
                props.updateStateVariable('year', value)
                setFirstName(value)
                break
            case 'brand':
                props.updateStateVariable('brand', value)
                setLastName(value)
                break
            case 'type':
                setType(value)
                break
            case 'price':
                setPrice(value)
                break
            case 'artistId':
                setArtistId(value)
                break
            default:
                break
        }
    }



    return (
        <Form
            form={form}
            name='update-artist-form'
            layout='inline'
            onFinish={onFinish}
            initialValues={{
                firstName: firstName,
                lastName: lastName
            }}
            size='large'
        >
            <Form.Item
                name='firstName'
                label='First Name'
                rules={[{ required: true, message: 'Please input your first name!' }]}
            >
                <Input
                    placeholder='i.e. John'
                    onChange={e => updateStateVariable('firstName', e.target.value)}
                />
            </Form.Item>
            <Form.Item
                name='lastName'
                label='Last Name'
                rules={[{ required: true, message: 'Please input your last name!' }]}
            >
                <Input
                    placeholder='i.e. Smith'
                    onChange={e => updateStateVariable('lastName', e.target.value)}
                />
            </Form.Item>
            <Form.Item shouldUpdate={true}>
                {() => (
                    <Button
                        type='primary'
                        htmlType='submit'
                        disabled={
                            !form.isFieldsTouched(true) ||
                            form.getFieldsError().filter(({ errors }) => errors.length).length
                        }
                    >
                        Update Artist
                    </Button>
                )}
            </Form.Item>
            <Button onClick={props.onButtonClick}>Cancel</Button>
        </Form>
    )
}

export default UpdateInstrument
